export default function Config(props) {
    const env = props.environment || "development";
    const timestamp = new Date().toISOString();
    // Computed values based on environment
    const replicas = env === "production" ? 3 : 1;
    const resources = env === "production"
        ? { requests: { cpu: "500m", memory: "512Mi" }, limits: { cpu: "1000m", memory: "1Gi" } }
        : { requests: { cpu: "100m", memory: "128Mi" } };
    return {
        apiVersion: "v1",
        kind: "ConfigMap",
        metadata: {
            name: `${props.app}-config`,
            labels: {
                app: props.app,
                version: props.version,
                environment: env
            },
            annotations: {
                "yakt.generated": timestamp,
                "yakt.version": props.version
            }
        },
        data: {
            APP_NAME: props.app,
            APP_VERSION: props.version,
            ENVIRONMENT: env,
            REPLICAS: replicas.toString(),
            GENERATED_AT: timestamp
        }
    };
}
